import React, {useEffect, useState} from "react"
import {ActuallyImg, AllNewsImg, BlogNews} from "../"
import {useSelector} from "react-redux";
import {AppState} from "../../store/store";
import {Blog} from "../../request/BlogApi"
import "./Home.scss"

const news = [
    {
        img: "data:image/jpeg;base64,",
        title: "Title",
        tags: ["#tag", "#tag"],
        text: "Lorem ipsum dolor"
    },
    {
        img: "data:image/jpeg;base64,",
        title: "Title2",
        tags: ["#tag2", "#tag2"],
        text: "2Lorem ipsum dolor"
    },
    {
        img: "data:image/jpeg;base64,",
        title: "Title3",
        tags: ["#tag3", "#tag3"],
        text: "3Lorem ipsum dolor"
    },
    {
        img: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSud9bFicE5S9XezjnmMTSW7SU5toRQDoaubg&usqp=CAU",
        title: "Title4",
        tags: ["#tag4", "#tag4"],
        text: "4Lorem ipsum dolor"
    },
]


export const Home = () => {
    const {blogs} = useSelector((state: AppState) => ({blogs: state?.blogState?.blogs}));
    const [actuallyNum, setActuallyNum] = useState<number>(0)
    const [actuallyNews, setActuallyNews] = useState<Blog>(news[0])
    console.log(blogs)
    const handleActually = (index: number) => {
        let num = actuallyNum + index

        if (num === news.length) {
            num = 0
        }

        setActuallyNum(num)
        setActuallyNews(news[num])
    }

    useEffect(() => {
        const interval = setInterval(() => {
            if (actuallyNum === news.length - 1) {
                return setActuallyNum(0)
            }

            return setActuallyNum(actuallyNum + 1)
        }, 8000)

        setActuallyNews(news[actuallyNum])

        return () => clearInterval(interval)
    }, [actuallyNews, actuallyNum])

    return (
        <div className="home">
            <div className="home__blog">
                <div className="home__blogWrapper">
                    {actuallyNews &&
                    <ActuallyImg blog={actuallyNews} handleActually={(index) => handleActually(index)}/>}
                    <AllNewsImg news={news}
                                actuallyNews={(index: number) => handleActually(index)}/>
                </div>
                {actuallyNews &&
                <BlogNews title={actuallyNews.title} tags={actuallyNews.tags} text={actuallyNews.text}/>}
            </div>
        </div>
    )
}
